import { useEffect, useState } from "react";

const parseStringToJson = (value) => {
  try {
    return JSON.parse(value)
  } catch (e) {
    return null
  }
}

const useLocalStorage = (key, fallbackValue) => {
  const [value, setValue] = useState(
    JSON.parse(window.localStorage.getItem(key) || JSON.stringify(fallbackValue)) || fallbackValue
  );
  useEffect(() => {
    window.localStorage.setItem(key, JSON.stringify(value));
    console.log(
      `[useLocalStorage]: ${key} updated with value ${window.localStorage.getItem(
        key
      )}`
    );
  }, [key, value]);
  return [value, setValue];
};

export default useLocalStorage;
